import React, { useState, useEffect } from 'react';
import  '../../styles/style.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (localStorage.getItem('token') !== null) {
      window.location.replace('http://localhost:3000/dashboard');
    } else {
      setLoading(false);
    }
  }, []);

  const onSubmit = e =>{
    e.preventDefault();

    const user = {
      username : username,
      password : password
    }
    var url = 'http://127.0.0.1:8000/api/auth/login'

    fetch( url , {
      method : 'post',
      headers : {'Content-Type': 'application/json'} ,
      body : JSON.stringify(user)
    })

    .then(data => data.json())
    .then(data => { 
      if(data){
        localStorage.clear()
        localStorage.setItem('token', data.token)
        console.log('login fetched userdata', data)
        window.location.replace('http://localhost:3000/')
      } else {
        setUsername('')
        setPassword('')
        localStorage.clear()
        setErrors(true)
      }
    })
  }
  return ( 
    <>
    {loading === false }

    {loading === false && (

      <div className="ui middle aligned center aligned grid">
        <div className="four wide column">
          <h2 className="ui teal image header">
            <div className="content">
              Log-in to your account
            </div>
          </h2>
          <form className="ui large form" onSubmit={onSubmit}>
          <div className="ui segment">
            <div className="field">
              <div className="ui left icon input">
                <i className="user icon"></i>

                <input name='username' type='text'
                  value={username} required onChange={e => setUsername(e.target.value)} />
                  {' '}
              </div>
            </div>
            <div className="field">
              <div className="ui left icon input">
                <i className="lock icon"></i>
                <input name='password' type='password'
                  value={password} required onChange={e => setPassword(e.target.value)} />
                  {' '}
              </div>
            </div>
            <button className="ui fluid large teal submit button" type="submit">Login</button>
          </div>   
      </form>

      <div className="ui message error">
        {errors === true && <h3>Incorrect Credentials</h3>}
      </div>
      <div className="ui message">
        New to us? <a href="#">Sign Up</a>
      </div>

    </div>
    </div>
  )}
  </>
  );
};
    
    export default Login;
